function alog(x,callBack){
  setTimeout(()=>{
    console.log(x);
    if(callBack){
      callBack();
    }
    
  },Math.random()*1000);
}

function plog(x){
  return new Promise((callBack,reject)=>{
    setTimeout(()=>{
      console.log(x);
      callBack();
    },Math.random()*1000);
  })
}

function a_add(x,y,callBack){
  setTimeout(()=>{
    if(callBack){
      callBack(x+y);
    }
  },Math.random()*1000);
}

function p_add(x,y){
  return new Promise((callBack,reject)=>{
    setTimeout(()=>{
      callBack(x+y);
    },Math.random()*1000);
  })
}

module.exports = {
  alog,
  plog,
  a_add,
  p_add
};
